/*

Author: NFN Neil
Description: Allows people to easily implement sortable table.

Simply add the class .sortable to any table and that table
is now sortable.  That simple.

*/

function makeArray(a){return buildingArray=[],a.find("thead tr th").each(function(a){$(this).attr("pos",a)}),a.find("tbody tr").each(function(){current=[],$(this).find("td").each(function(){current.push($(this).html())}),buildingArray.push(current)}),buildingArray}$("table.sortable").find("th").click(function(){table=$(this).parents("table.sortable"),arr=makeArray(table),index=Number($(this).attr("pos")),arr.sort(function(a,b){return a[index].localeCompare(b[index])}),temp="";for(var a=0,b=arr.length;a<b;a++){temp+="<tr>";for(var c=0,d=arr[a].length;c<d;c++)temp+="<td>"+arr[a][c]+"</td>";temp+="</tr>"}$(this).parents("table:first").find("tbody").html(temp)});